import { useState } from "react";
import { motion } from "framer-motion";
import profilePic from "./assets/Photo.jpeg";

export default function App() {
  const [active, setActive] = useState("about");
  const sections = [
    { id: "about", label: "About" },
    { id: "skills", label: "Skills" },
    { id: "experience", label: "Experience" },
    { id: "projects", label: "Projects" },
    { id: "achievements", label: "Achievements" },
    { id: "contact", label: "Contact" }
  ];

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800">
      <nav className="fixed top-0 left-0 w-full bg-white shadow-md z-50 flex justify-between items-center px-8 py-4">
        <h1 className="text-2xl font-bold text-blue-600">Shikhar Agarwal</h1>
        <div className="space-x-6">
          {sections.map((s) => (
            <button
              key={s.id}
              onClick={() => {
                setActive(s.id);
                document.getElementById(s.id)?.scrollIntoView({ behavior: "smooth" });
              }}
              className={
                "font-medium hover:text-blue-600 " +
                (active === s.id ? "text-blue-600" : "text-gray-700")
              }
            >
              {s.label}
            </button>
          ))}
        </div>
      </nav>

      <main className="pt-24 space-y-24 px-10 md:px-32">
        <section id="about" className="flex flex-col md:flex-row items-center gap-10">
          <motion.img
            src={profilePic}
            alt="Shikhar Agarwal"
            className="rounded-full w-48 h-48 object-cover shadow-lg border-4 border-blue-500"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
          />
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
            <h2 className="text-3xl font-bold text-blue-600 mb-3">Aspiring Software Engineer</h2>
            <p className="text-lg text-gray-700 max-w-2xl">
              Highly motivated software developer skilled in Java, Web Development, and OOPS.
              Passionate about building efficient and impactful digital solutions.
            </p>
            <div className="mt-5 space-x-4">
              <a href="https://www.linkedin.com/in/shikhar-agarwal-5a9a50265/" target="_blank" rel="noreferrer">
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg shadow hover:bg-blue-700">LinkedIn</button>
              </a>
              <a href="mailto:shikharag2005@gmail.com">
                <button className="border border-blue-600 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-50">Email Me</button>
              </a>
            </div>
          </motion.div>
        </section>
      </main>
    </div>
  );
}
